INSERT INTO Pais VALUES (01,'Mexico','Es un país de América ubicado en la parte meridional de América del Norte.');
	INSERT INTO Ciudad VALUES (01,01 ,'Ciudad de Mexico','Se localiza en el Valle de México, a una altitud media de 2,240 msnm. Tiene una superficie de 1,495 km2, y se divide administrativamente en 16 delegaciones. Su población es de 8.9 millones de habitantes aproximadamente.');
	INSERT INTO Ciudad VALUES (01,02 ,'Ecatepec ','Se ubica al noreste de la entidad y al norte del valle de México, limitando al sur con la Ciudad de México y siendo parte de la zona metropolitana del Valle de México. Su cabecera es la localidad de San Cristóbal Ecatepec.');
	INSERT INTO Ciudad VALUES (01,03 ,'Guadalajara ','Guadalajara es una ciudad y municipio mexicano, capital y urbe más poblada del estado de Jalisco. Se localiza en el occidente de México, al centro de Jalisco, en la zona geográfica conocida como Valle de Atemajac.');
	INSERT INTO Ciudad VALUES (01,04 ,'Puebla de Zaragoza','Denominada oficialmente como Heroica Puebla de Zaragoza,8​ es una metrópoli mexicana, capital y ciudad más poblada del estado de Puebla, así como cabecera del municipio que lleva su nombre y de la Zona Metropolitana de Puebla-Tlaxcala.');
	INSERT INTO Ciudad VALUES (01,05 ,'Ciudad Juárez','Ciudad Juárez es una ciudad de México situada en el norte del país, en el estado de Chihuahua, a orillas del río Bravo. Al otro lado del río, en territorio estadounidense, se encuentra la ciudad de El Paso (Texas).');
INSERT INTO Pais VALUES (02,'Argentina','Es un país soberano de América del Sur, ubicado en el extremo sur y sudeste de dicho subcontinente.');
	INSERT INTO Ciudad VALUES (02,06 ,'Buenos Aires','Buenos Aires, denominada también, oficialmente, Ciudad Autónoma de Buenos Aires,5​ es la capital de la República Argentina y el principal núcleo urbano del país.');
	INSERT INTO Ciudad VALUES (02,07 ,'Córdoba','Se encuentra ubicada en la región central del país, a ambas orillas del río Suquía. Es la segunda ciudad más poblada después de Buenos Aires y la más extensa del país.');
	INSERT INTO Ciudad VALUES (02,08 ,'Rosario',' Es la ciudad más poblada de la provincia de Santa Fe y la tercera ciudad más poblada del país, solo detrás de Buenos Aires y Córdoba. Es la cabecera del departamento homónimo.');
	INSERT INTO Ciudad VALUES (02,09 ,'La Plata','La Plata es una ciudad argentina, capital de la provincia de Buenos Aires y cabecera del partido homónimo. Se ubica a 56 km al sudeste de la ciudad de Buenos Aires');
	INSERT INTO Ciudad VALUES (02,10 ,'Salta','Salta es una ciudad, un municipio y la capital de la provincia de Salta, República Argentina, que tiene una población de 535 303 habitantes, siendo la ciudad más poblada de la provincia y la séptima del país.');
INSERT INTO Pais VALUES (03,'España','E​s un país soberano, miembro de la Unión Europea.');
	INSERT INTO Ciudad VALUES (03,11 ,'Madrid','La localidad, con categoría histórica de villa, es la capital del Estado​ y de la Comunidad de Madrid. También conocida como la Villa y Corte, es la ciudad más poblada del país.');
	INSERT INTO Ciudad VALUES (03,12 ,'Barcelona','Barcelona es una ciudad española, capital de la comunidad autónoma de Cataluña, de la comarca del Barcelonés y de la provincia homónima.');
	INSERT INTO Ciudad VALUES (03,13 ,'Valencia','Valencia (en valenciano y oficialmente València) es un municipio ​y una ciudad de España, capital de la provincia homónima y de la Comunidad Valenciana.');
	INSERT INTO Ciudad VALUES (03,14 ,'Sevilla','Su casco antiguo es el más extenso de España y uno de los tres más grandes de toda Europa junto a los de Venecia y Génova, con 3,94 kilómetros cuadrados, y su casco histórico uno de los más grandes de España.');
	INSERT INTO Ciudad VALUES (03,15 ,'Zaragoza','Zaragoza es una ciudad y un municipio de España, capital de la comarca de Zaragoza, de la provincia homónima y de la comunidad autónoma de Aragón.');
INSERT INTO Pais VALUES (04,'Colombia','Es un país soberano situado en la región noroccidental de América del Sur.');
	INSERT INTO Ciudad VALUES (04,16 ,'Bogotá','Bogotá fue fundada como capital del Nuevo Reino de Granada el 6 de agosto de 1538 por el conquistador español Gonzalo Jiménez de Quesada después de una dura expedición a los Andes.');
	INSERT INTO Ciudad VALUES (04,17 ,'Medellín','Medellín es un municipio colombiano, capital del departamento de Antioquia. Es la ciudad más poblada del departamento y la segunda del país.');
	INSERT INTO Ciudad VALUES (04,18 ,'Cali','Está situada en la región Sur del Valle del Cauca, entre la cordillera occidental y la cordillera central de los Andes.');
	INSERT INTO Ciudad VALUES (04,19 ,'Barranquilla',' Está ubicada sobre la margen occidental del río Magdalena a 7,5 km de su desembocadura en el mar Caribe. En 1993 fue organizada constitucionalmente en distrito especial, industrial y portuario.');
	INSERT INTO Ciudad VALUES (04,20 ,'Cartagena','Cartagena de Indias, oficialmente Distrito Turístico y Cultural de Cartagena de Indias abreviado Cartagena de Indias, D. T. y C., es la capital del departamento de Bolívar, Colombia.');
INSERT INTO Pais VALUES (05,'Estados Unidos','Es un país soberano constituido en república federal constitucional compuesta por cincuenta estados y un distrito federal.');
	INSERT INTO Ciudad VALUES (05,21 ,'Nueva York','Es la ciudad más poblada del estado homónimo y de los Estados Unidos de América, y la segunda mayor concentración urbana del continente americano, después de la Ciudad de México.');
	INSERT INTO Ciudad VALUES (05,22 ,'Los Ángeles','Ciudad de Los Ángeles y de manera abreviada L. A., es la ciudad más poblada del estado estadounidense de California y la segunda ciudad más poblada de Estados Unidos. Tiene, según el censo de 2010, una población de 3 792 621 habitantes.');
	INSERT INTO Ciudad VALUES (05,23 ,'Chicago','Chicago, conocida coloquialmente como «la Segunda Ciudad» o «la Ciudad de los Vientos», es la tercera ciudad con mayor número de habitantes en Estados Unidos, detrás de Nueva York y Los Ángeles.');
	INSERT INTO Ciudad VALUES (05,24 ,'Dallas','Dallas es la ciudad más grande en los Estados Unidos sin conexión al mar debido a su ubicación central y a su gran industria en tecnologías de la información.');
	INSERT INTO Ciudad VALUES (05,25 ,'Filadelfia','Es un gran centro histórico, cultural y artístico en los Estados Unidos, y de la misma forma un importante puerto industrial sobre el río Delaware, que se extiende hasta el océano Atlántico.');
INSERT INTO Pais VALUES (06,'Francia','Francia, en Europa Occidental, abarca ciudades medievales, villas alpinas y playas mediterráneas. París, su capital, es famosa por sus firmas de alta costura, los museos de arte clásico, como el Louvre, y monumentos como la Torre Eiffel. ');
	INSERT INTO Ciudad VALUES (06,26 ,'París','Está situada a ambos márgenes de un largo meandro del río Sena, en el centro de la cuenca parisina, entre la confluencia del río Marne y el Sena, aguas arriba, y el Oise y el Sena, aguas abajo.');
	INSERT INTO Ciudad VALUES (06,27 ,'Marsella','Marsella es la segunda ciudad más poblada de Francia con 859 543 habitantes, principal centro económico y mayor metrópoli del Mediodía francés.');
	INSERT INTO Ciudad VALUES (06,28 ,'Lyon','Antigua capital de la Galia durante el Imperio romano; durante la Edad Media, Lyon se convirtió en una ciudad comercial y después en el siglo XIX en una plaza financiera de primer orden.');
	INSERT INTO Ciudad VALUES (06,29 ,'Toulouse','Recibe el apodo de Ciudad Rosa por el color dominante en los edificios antiguos, hechos con ladrillos caravista. Su altitud promedio es de 141 metros, el relieve es poco accidentado.');
	INSERT INTO Ciudad VALUES (06,30 ,'Niza','Es una ciudad francesa situada en el departamento de los Alpes Marítimos, en la región de Provenza-Alpes-Costa Azul.');
INSERT INTO Pais VALUES (07,'Brasil','Es un país soberano de América del Sur que comprende la mitad oriental del subcontinente y algunos grupos de pequeñas islas en el océano Atlántico.');
	INSERT INTO Ciudad VALUES (07,31 ,'Sao Paulo','Es la mayor ciudad de América del Sur y el principal centro financiero de Brasil. Algunas fuentes la ubican como la mejor ciudad para hacer negocios en América Latina.');
	INSERT INTO Ciudad VALUES (07,32 ,'Río de Janeiro','Fue la capital del Reino de Portugal entre 1807 y 1822 y la capital del Imperio del Brasil desde 1822, cuando la nación declaró su independencia del país luso y conservó ese rango hasta la inauguración de Brasilia, en 1960.');
	INSERT INTO Ciudad VALUES (07,33 ,'Salvador','Situada en la Microrregión de Salvador, la ciudad es una metrópoli regional con casi tres millones de habitantes, siendo la ciudad más poblada del Noreste de Brasil, la tercera más poblada de dicho país.');
	INSERT INTO Ciudad VALUES (07,34 ,'Brasilia','Es la capital federal del Brasil y la sede del gobierno del Distrito Federal, localizada en la región Centro-Oeste del país.');
	INSERT INTO Ciudad VALUES (07,35 ,'Fortaleza','Fortaleza es un municipio de Brasil y ciudad capital del estado de Ceará. Importante centro turístico, comercial y financiero, es una de las tres ciudades más importantes de la región Nordeste de Brasil, junto a Recife y Salvador.');
INSERT INTO Pais VALUES (08,'Italia','Italia, país europeo con una larga costa mediterránea, influyó considerablemente en la cultura y la cocina occidental. ');
	INSERT INTO Ciudad VALUES (08,36 ,'Roma','En el transcurso de su historia, que abarca tres milenios, llegó a extender sus dominios sobre toda la cuenca del Mediterráneo y gran parte de Europa.');
	INSERT INTO Ciudad VALUES (08,37 ,'Milán','Se encuentra ubicada en la llanura padana, una de las regiones más desarrolladas de Italia.');
	INSERT INTO Ciudad VALUES (08,38 ,'Nápoles','Tiene una gran riqueza histórica, artística, cultural y gastronómica, lo que llevó a la Unesco a declarar su centro histórico Patrimonio de la Humanidad.');
	INSERT INTO Ciudad VALUES (08,39 ,'Turín',' El área metropolitana de Turín, según la Organización para la Cooperación y el Desarrollo Económico, tiene una población de alrededor de 2.200.000 habitantes');
	INSERT INTO Ciudad VALUES (08,40 ,'Palermo','Para actividades culturales, artísticas y económicas fue una de las ciudades más grandes del Mediterráneo y hoy es un apreciado destino estival para el turista que visita tierras italianas.');
INSERT INTO Pais VALUES (09,'Peru','Es un país soberano del oeste de América del Sur.');
	INSERT INTO Ciudad VALUES (09,41 ,'Lima','Se encuentra situada en la costa central del país, a orillas del océano Pacífico, conformando una extensa y populosa área urbana conocida como Lima Metropolitana.');
	INSERT INTO Ciudad VALUES (09,42 ,'Arequipa','También conocida como la «Ciudad Blanca», es la segunda ciudad del Perú por población, alcanzando el año 2015 los 869 351 habitantes según proyecciones del INEI (Instituto Nacional de Estadística e Informática).');
	INSERT INTO Ciudad VALUES (09,43 ,'Trujillo','La ciudad capital se encuentra ubicada en la costa norte peruana, a una altitud media de 34 msnm en la margen derecha del río Moche a orillas del Océano Pacífico, en el Valle de Moche o Santa Catalina.');
	INSERT INTO Ciudad VALUES (09,44 ,'Chiclayo','Es una ciudad del noroeste peruano, capital de la provincia homónima y del departamento de Lambayeque. Chiclayo, fue fundada con el nombre de «Santa María de los Valles de Chiclayo».');
	INSERT INTO Ciudad VALUES (09,45 ,'Iquitos','Las mayores industrias extracción de la ciudad están conformadas por la maderera, pesquera, petrolera, minera y agrícola, y tiene un alta actividad económica en el turismo, la artesanía, la panadería y la bebidas gasificadas y cervecería.');
INSERT INTO Pais VALUES (10,'Japon','Japón es una nación insular del océano Pacífico con densas ciudades, palacios imperiales, parques nacionales montañosos y miles de santuarios y templos.');
	INSERT INTO Ciudad VALUES (10,46 ,'Tokio','Cuenta también con la mayor concentración de sedes corporativas, instituciones financieras, universidades y colegios, museos, teatros, y establecimientos comerciales y de entretenimiento de todo Japón.');
	INSERT INTO Ciudad VALUES (10,47 ,'Kanagawa','Se cree que la dinastía Yamato gobernó esta área en el siglo 5 en adelante. En la era ancestral, sus llaneras estaban habitadas muy escasamente.');
	INSERT INTO Ciudad VALUES (10,48 ,'Osaka','Para la historia de Osaka anterior a la Restauración Meiji, ver Osaka, Provincia de Izumi, Provincia de Kawachi y Provincia de Settsu.');
	INSERT INTO Ciudad VALUES (10,49 ,'Aichi','Originalmente la región se encontraba dividida en las tres provincias de Owari, Mikawa y Ho. Luego de la era Taika, Mikawa y Ho fueron unidos en una única entidad.');
	INSERT INTO Ciudad VALUES (10,50 ,'Sapporo','Presenta temperaturas más bajas que el resto de ciudades japonesas debido a su clima continental húmedo, con continuas nevadas en los meses de invierno y tiempo suave durante el verano.');
INSERT INTO Pais VALUES (11,'Chile','Chile es un país largo y angosto que se extiende por el borde occidental de Sudamérica, con más de 6,000 km de costa en el océano Pacífico.');
	INSERT INTO Ciudad VALUES (11,51 ,'Santiago de Chile','Es la capital de Chile y6​ de la región Metropolitana de Santiago. Es el principal núcleo urbano del país y su área metropolitana se conoce también con el nombre de Gran Santiago.');
	INSERT INTO Ciudad VALUES (11,52 ,'Gran Concepción','Su población se estima en aproximadamente 1 083 000 habitantes, convirtiéndola junto al Gran Valparaíso en la segunda zona metropolitana más poblada del país, después de Santiago.');
	INSERT INTO Ciudad VALUES (11,53 ,'Gran Valparaíso','Su importancia radica también en ser sede del Congreso Nacional, un considerable polo de industrias y servicios, un gran centro universitario y en tener uno de los circuitos turísticos más importantes del país.');
	INSERT INTO Ciudad VALUES (11,54 ,'Gran Coquimbo','Esta es la cuarta área metropolitana más poblada de Chile, por detrás de Gran Santiago, Gran Valparaíso y Gran Concepción. Representa el 2,5% del total de la población chilena.');
	INSERT INTO Ciudad VALUES (11,55 ,'Antofagasta','Antofagasta, apodada como La Perla del Norte, es una ciudad, puerto y comuna del norte de Chile, capital de la provincia y de la región homónima.');
INSERT INTO Pais VALUES (12,'Alemania','Alemania es un país de Europa occidental con un paisaje de bosques, ríos, cadenas montañosas y playas en el mar del Norte.');
	INSERT INTO Ciudad VALUES (12,56 ,'Berlín','Berlín es una ciudad mundial y un centro cultural y artístico de primer nivel. Es una de las ciudades más influyentes en el ámbito político de la Unión Europea y en 2006 fue elegida Ciudad Creativa por la Unesco.');
	INSERT INTO Ciudad VALUES (12,57 ,'Hamburgo','El nombre completo de Hamburgo es «Ciudad libre y hanseática de Hamburgo» (Freie und Hansestadt Hamburg en alemán).');
	INSERT INTO Ciudad VALUES (12,58 ,'Múnich','El lema de la ciudad es München mag Dich (A Múnich le gustas). Antes de 2006 era Weltstadt mit Herz (Ciudad cosmopolita con corazón).');
	INSERT INTO Ciudad VALUES (12,59 ,'Colonia','Es un importante centro económico y cultural de importancia internacional y representa uno de los enclaves más importantes del mundo artístico.');
	INSERT INTO Ciudad VALUES (12,60 ,'Fráncfort','​Aunque la capital administrativa de Hesse es Wiesbaden, Fráncfort tiene una importancia económica mayor.');
INSERT INTO Pais VALUES (13,'Inglaterra','Inglaterra, la cuna de Shakespeare y The Beatles, es un país de las Islas Británicas que limita con Escocia y Gales.');
	INSERT INTO Ciudad VALUES (13,61 ,'Londres','Situada a orillas del río Támesis, Londres es un importante asentamiento humano desde que fue fundada por los romanos con el nombre de Londinium hace casi dos milenios.');
	INSERT INTO Ciudad VALUES (13,62 ,'Birmingham','La reputación de la ciudad se forjó como la locomotora de la Revolución industrial en Reino Unido, siendo conocida como "El taller del mundo" o la "Ciudad de los mil oficios".');
	INSERT INTO Ciudad VALUES (13,63 ,'Glasgow','La gente de Glasgow es conocida como Glaswegians. Asimismo, Glaswegian es el nombre con que se conoce al dialecto local escocés.');
	INSERT INTO Ciudad VALUES (13,64 ,'Liverpool','La ciudad se encuentra en el centro del Área Metropolitana de Liverpool, cuya población asciende a 816 216 habitantes.');
	INSERT INTO Ciudad VALUES (13,65 ,'Leeds','Antiguamente formaba parte del West Riding of Yorkshire, la historia escrita de Leeds se remonta al siglo V cuando el reino de Elmet estaba cubierto por bosques de "Loidis", lo que dio origen al nombre Leeds');
INSERT INTO Pais VALUES (14,'Canada','Canadá es el país norteamericano que se extiende desde los Estados Unidos en el sur hasta el círculo polar ártico en el norte.');
	INSERT INTO Ciudad VALUES (14,66 ,'Airdrie','Airdrie es una ciudad de Alberta, en Canadá, situada justo al norte de Calgary sobre el corredor Calgary-Edmonton. Forma parte de la zona de censo de la région metropolitana de Calgary y es miembro de Calgary Regional Partnership.');
	INSERT INTO Ciudad VALUES (14,67 ,'Calgary','Se encuentra ubicada en una región de colinas y altiplanicies, aproximadamente 80 km al este de las Montañas Rocosas.');
	INSERT INTO Ciudad VALUES (14,68 ,'Edmonton','Está ubicada en la parte central de la provincia, una de las zonas más fértiles de las llanuras canadienses, en torno al río Saskatchewan Norte. 812.201 personas viven en Edmonton.');
	INSERT INTO Ciudad VALUES (14,69 ,'Leduc','Leduc es una ciudad en la provincia de Alberta, Canadá. Se encuentra a 33 kilómetros al sur de la capital provincial Edmonton y forma parte de su área metropolitana.');
	INSERT INTO Ciudad VALUES (14,70 ,'Red Deer','Red Deer es la tercera ciudad más grande en la provincia canadiense de Alberta, detrás de Calgary y Edmonton');
INSERT INTO Pais VALUES (15,'China','China es una nación muy poblada de Asia Oriental cuyos vastos paisajes abarcan praderas, desiertos, montañas, lagos, ríos y más de 14,000 km de costa.');
	INSERT INTO Ciudad VALUES (15,71 ,'Shanghái','El área donde se sitúa la ciudad fue colonizada y asentada por los refugiados que huían de los mongoles hacia el 960-1126 d. C.');
	INSERT INTO Ciudad VALUES (15,72 ,'Pekín','Pekín es la capital del país y una de las ciudades más pobladas del mundo con 21 150 000 personas en 2013.');
	INSERT INTO Ciudad VALUES (15,73 ,'Cantón','Actualmente es la conurbación más grande de la Tierra, con 46 900 000 habitantes.');
	INSERT INTO Ciudad VALUES (15,74 ,'Shenzhen','El nombre de la ciudad tiene su origen en la denominación que daban los habitantes de la zona a las zanjas de los arrozales a las que llamaban zhen o chon.');
	INSERT INTO Ciudad VALUES (15,75 ,'Tianjin','Es la cuarta ciudad más poblada de China tras Shanghái, Beijing y Guangzhou, con más de cuatro millones de habitantes en 170 kilómetros cuadrados que hacen un total de 15 469 500 habitantes.');
INSERT INTO Pais VALUES (16,'Rusia','Rusia es la nación más grande del mundo y limita con países de Europa y Asia, al igual que con los océanos Pacífico y Ártico.');
	INSERT INTO Ciudad VALUES (16,76 ,'Moscú','Moscú está situada a orillas del río Moscova, en el Distrito Federal Central de la Rusia europea.');
	INSERT INTO Ciudad VALUES (16,77 ,'San Petersburgo',' Conocida también como la “Venecia del Norte”, debido a sus más de 400 puentes que atraviesan a los numerosos canales que por ella pasan.');
	INSERT INTO Ciudad VALUES (16,78 ,'Novosibirsk','Con 1 473 737 habs. en 2010 es la tercera más poblada del país, por detrás de Moscú y San Petersburgo.');
	INSERT INTO Ciudad VALUES (16,79 ,'Ekaterimburgo','Es una ciudad ubicada en el centro-oeste de Rusia, capital del óblast de Sverdlovsk y del distrito federal del Ural.');
	INSERT INTO Ciudad VALUES (16,80 ,'Nizhni Nóvgorod','Con 1 250 615 habs. en 2010 es la quinta más poblada del país, por detrás de Moscú, San Petersburgo, Novosibirsk y Ekaterimburgo.');
INSERT INTO Pais VALUES (17,'Ecuador','Ecuador es un país que se extiende por el ecuador en la costa oeste de Sudamérica.');
	INSERT INTO Ciudad VALUES (17,81 ,'Guayaquil',' El área urbana de Guayaquil, también conocida como Gran Guayaquil, se alinea entre las ciudades más grandes de América.');
	INSERT INTO Ciudad VALUES (17,82 ,'Quito','La fecha de su primera fundación es incierta; los registros más antiguos se hallan en la hacienda del Inga alrededor del año 10300 a.c.');
	INSERT INTO Ciudad VALUES (17,83 ,'Cuenca','s llamada Atenas de Ecuador por su arquitectura, su diversidad cultural, su aporte a las artes, ciencias y letras ecuatorianas y por ser el lugar de nacimiento de muchos personajes ilustres de la sociedad ecuatoriana.');
	INSERT INTO Ciudad VALUES (17,84 ,'Santo Domingo','La ciudad es el centro político-administrativo de la provincia y uno de los principales del país. Alberga grandes organismos culturales, financieros, administrativos y comerciales.');
	INSERT INTO Ciudad VALUES (17,85 ,'Machala','Machala o San Antonio de Machala es una ciudad ecuatoriana, capital de la Provincia de El Oro. Es la cuarta ciudad más importante del país económicamente y el segundo puerto marítimo.');
INSERT INTO Pais VALUES (18,'Australia','Australia es un país rodeado por los océanos Índico y Pacífico. Sus ciudades principales (Sídney, Brisbane, Melbourne, Perth y Adelaida) son costeras.');
	INSERT INTO Ciudad VALUES (18,86 ,'Sídney','​Es la capital del estado de Nueva Gales del Sur y fue el asentamiento de la primera colonia británica en Australia.');
	INSERT INTO Ciudad VALUES (18,87 ,'Melbourne',' Fue la capital de Australia entre 1901 y 1927, cuando se trasladó a la ciudad planeada de Canberra.');
	INSERT INTO Ciudad VALUES (18,88 ,'Brisbane','Con 2 274 560 habs. en 2014 es la tercera ciudad más poblada del país, después de Sídney y Melbourne.');
	INSERT INTO Ciudad VALUES (18,89 ,'Perth','Tiene 1 659 000 habitantes,1​ lo que la convierte en la cuarta ciudad más poblada de Australia y la mayor del estado, ya que en ella residen casi tres cuartos de la población total del mismo.');
	INSERT INTO Ciudad VALUES (18,90 ,'Adelaida','Es una ciudad costera situada en la parte sur del océano Índico, en los Altos de Adelaida, al norte de la península de Fleurieu, entre el golfo de San Vicente y las Montañas Lofty Ranges. Tiene un plano bastante lineal.');
INSERT INTO Pais VALUES (19,'Venezuela','Venezuela es un país de la costa norte de América del Sur, con diversas atracciones naturales.');
	INSERT INTO Ciudad VALUES (19,91 ,'Caracas','Se encuentra ubicada en la zona centro-norte costera del país, a 15 km de la costa del mar Caribe y se sitúa dentro de un valle montañoso a una altitud promedio de 900 m s. n. m.');
	INSERT INTO Ciudad VALUES (19,92 ,'Maracaibo','Maracaibo es una ciudad venezolana, capital del estado Zulia, ubicada en el noroeste del país.');
	INSERT INTO Ciudad VALUES (19,93 ,'Valencia','La ciudad de Valencia es conocida como Capital Industrial de Venezuela debido a que alberga una cantidad de zonas Industriales del país.');
	INSERT INTO Ciudad VALUES (19,94 ,'Barquisimeto','Barquisimeto es una ciudad ubicada al occidente de Venezuela, mejor conocida como la ciudad musical del país. Capital del estado Lara, y del Municipio Iribarren.');
	INSERT INTO Ciudad VALUES (19,95 ,'Maracay','Maracay es una ciudad venezolana, capital del municipio Atanasio Girardot y del estado Aragua.');
INSERT INTO Pais VALUES (20,'Irlanda','La República de Irlanda ocupa la mayor parte de la isla de Irlanda, frente a las costas de Inglaterra y Gales.');
	INSERT INTO Ciudad VALUES (20,96 ,'Dublín','Está ubicada cerca del centro de la costa este sobre el mar de Irlanda, en la desembocadura del río Liffey y en el centro del condado de Dublín.');
	INSERT INTO Ciudad VALUES (20,97 ,'Galway','Galway, una ciudad puerto en la costa oeste de Irlanda, se ubica donde el río Corrib se une con el océano Atlántico.');
	INSERT INTO Ciudad VALUES (20,98 ,'Cork','Cork, justo al interior de la costa suroeste de Irlanda, es una ciudad universitaria con su centro en una isla en el río Lee, que se conecta con el mar mediante el puerto de Cork.');
	INSERT INTO Ciudad VALUES (20,99 ,'Limerick','En su compacto casco antiguo destacan la catedral medieval de Santa María y la plaza de San Juan, que está rodeada de casas adosadas georgianas.');
	INSERT INTO Ciudad VALUES (20,100 ,'Belfast','Es la cuna del RMS Titanic, famoso por haberse hundido tras estrellarse contra un témpano en 1912.');
